// src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import { Line, Bar } from 'react-chartjs-2';
import Sidebar from './Sidebar';
import '../index.css';

const Dashboard = () => {
  const [scoreData, setScoreData] = useState([]);
  const [severityData, setSeverityData] = useState([0, 0, 0, 0]);
  const [entropyData, setEntropyData] = useState([]);
  const [entropyLabels, setEntropyLabels] = useState([]);
  const [scoreLabels, setScoreLabels] = useState([]);
  const [topNotableSources, setTopNotableSources] = useState([]);
  const [recentDetections, setRecentDetections] = useState([]);
  const [shaDetections, setShaDetections] = useState([]);
  const [threatHuntingLeads, setThreatHuntingLeads] = useState({ investigated: 0, generated: 0 }); // New state for threat hunting leads
  const [threatIntelligence, setThreatIntelligence] = useState([]);
  const [alertColor, setAlertColor] = useState('#606060');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // State for sidebar visibility

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:5001/ws');

    ws.onopen = () => {
      console.log('WebSocket connection opened');
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log('Received data:', data);

      setAlertColor(getAlertColor(data.network_alert));
      updateRealtimeChart(data.entropy || 0); // Add default value for entropy
      setTopNotableSources(data.top_notable_sources || []); // Add default value for top_notable_sources
      setSeverityData(data.detection_severity || [0, 0, 0, 0]); // Add default value for detection_severity
      updateScoreChart(data.current_score || 0); // Add default value for current_score
      setRecentDetections(data.most_recent_detections || []); // Add default value for most_recent_detections
      setShaDetections(data.sha_detections || []); // Set SHA-based detections
      setThreatHuntingLeads({ 
        investigated: data.threat_hunting_leads_investigated || 0, 
        generated: data.threat_hunting_leads_generated || 0 
      }); // Set Threat Hunting Leads
      setThreatIntelligence(data.threat_intelligence || []);
    };

    ws.onclose = () => {
      console.log('WebSocket connection closed');
    };

    ws.onerror = (error) => {
      console.log('WebSocket error:', error);
    };

    return () => {
      ws.close(); // Clean up WebSocket connection on unmount
    };
  }, []);

  const getAlertColor = (level) => {
    switch (level) {
      case 'Fetching Data': return '#606060';
      case 'Low': return '#008000';
      case 'Medium': return '#FFA500';
      case 'High': return '#FF4500';
      case 'HIGH ALERT!': return '#B22222';
      default: return '#606060';
    }
  };

  const updateRealtimeChart = (value) => {
    setEntropyData(prevData => {
      const newData = [...prevData, value];
      return newData.length > 7 ? newData.slice(1) : newData; // Keep last 8 data points (40 seconds of data at 5-second intervals)
    });
    setEntropyLabels(prevLabels => {
      const newLabels = [...prevLabels, new Date().toLocaleTimeString()];
      return newLabels.length > 8? newLabels.slice(1) : newLabels;
    });
  };

  const updateScoreChart = (score) => {
    setScoreData(prevData => {
      const newData = [...prevData, score];
      return newData.length > 7 ? newData.slice(1) : newData;
    });
    setScoreLabels((prevLabels) => {
      const newLabels = [...prevLabels, new Date().toLocaleTimeString()];
      return newLabels.length > 8? newLabels.slice(1) : newLabels;
    });
  };

  const scoreChartData = {
    labels: scoreLabels,
    datasets: [{
      label: 'Current Score',
      data: scoreData,
      backgroundColor: 'rgba(255, 99, 132, 0.2)',
      borderColor: 'rgba(255, 99, 132, 1)',
      borderWidth: 2,
      pointRadius: 0, // Remove dots
      fill: false
    }]
  };

  const severityChartData = {
    labels: ['Low', 'Medium', 'High', 'Critical'],
    datasets: [{
      label: 'Detections by Severity',
      data: severityData,
      backgroundColor: [
        'rgba(0, 0, 255, 0.75)', // Bolder blue
        'rgba(255, 165, 0, 0.75)', // Bolder orange
        'rgba(255, 69, 0, 0.75)', // Bolder orange red
        'rgba(255, 0, 0, 0.75)' // Bolder red
      ],
      borderColor: [
        'rgba(0, 0, 255, 0.5)', // Bolder blue
        'rgba(255, 165, 0, 0.5)', // Bolder orange
        'rgba(255, 69, 0, 0.5)', // Bolder orange red
        'rgba(255, 0, 0, 0.5)' // Bolder red
      ],
      borderWidth: 1
    }]
  };

  const entropyChartData = {
    labels: entropyLabels,
    datasets: [{
      label: 'Entropy',
      data: entropyData,
      backgroundColor: 'rgba(54, 162, 235, 0.5)',
      borderColor: 'rgba(0, 0, 139, 1)', // Darker blue
      borderWidth: 2,
      pointRadius: 0, // Remove dots
      fill: false
    }]
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false // Hide legend
      }
    },
    scales: {
      x: {
        grid: {
          display: false // Hide X grid lines
        }
      },
      y: {
        grid: {
          display: false // Hide Y grid lines
        }
      }
    }
  };

  const entropyChartOptions = {
    ...chartOptions,
    scales: {
      ...chartOptions.scales,
      y: {
        ...chartOptions.scales.y,
        min: -1,
        max: 1
      }
    },
    layout: {
      padding: {
        top: 20,
        bottom: 35 // Adjust padding to reduce the height of the chart
      }
    }
  };

  return (
    <div>
      <header>
        <div className="logo">
          <a href="#" onClick={() => setIsSidebarOpen(true)}>Omen & Weave</a>
        </div>
        <nav>
          <ul>
            <li><a href="#">Platform</a></li>
            <li><a href="#">Services</a></li>
            <li><a href="#">Why Omen & Weave</a></li>
            <li><a href="#">Learn</a></li>
            <li><a href="#">Company</a></li>
          </ul>
        </nav>
        <div className="header-buttons">
          <button>Contact Us</button>
          <button>View Bundles & Pricing</button>
        </div>
      </header>
      <Sidebar isOpen={isSidebarOpen} onClose={() => setIsSidebarOpen(false)} />
      <div className="dashboard-header">
        <h1>Detections Dashboard</h1>
        <div id="networkStatus" className="circle" style={{ backgroundColor: alertColor }}></div>
      </div>
      <div className="dashboard">
        <div className="module" id="currentScore">
          <h2>Current OWL Score</h2>
          <div className="chart-container">
            <Line data={scoreChartData} options={chartOptions} />
          </div>
        </div>
        <div className="module" id="topNotableSources">
          <h2>Top Notable Sources</h2>
          <ul>
            {topNotableSources.map((source, index) => (
              <li key={index}>{source}</li>
            ))}
          </ul>
        </div>
        <div className="module" id="shaDetections">
          <h2>SHA-based Detections</h2>
          <ul>
            {shaDetections.map((detection, index) => (
              <li key={index}>{detection}</li>
            ))}
          </ul>
        </div>
        <div className="module" id="threatHuntingLeads">
          <h2>Threat Hunting Leads</h2>
          <div>
            Investigated / Generated: {threatHuntingLeads.investigated} / {threatHuntingLeads.generated}
          </div>
        </div>
        <div className="module chart-container" id="entropyOverTime">
          <h2>Entropy Over Time</h2>
          <Line data={entropyChartData} options={entropyChartOptions} />
        </div>
        <div className="module" id="detectionSeverity">
          <h2>Detections by Severity</h2>
          <div className="chart-container">
            <Bar data={severityChartData} options={chartOptions} />
          </div>
        </div>
        <div className="module" id="threatIntelligence">
          <h2>Threat Intelligence</h2>
          <ul>
            {threatIntelligence.map((intelligence, index) => (
              <li key={index}>{intelligence}</li>
            ))}
          </ul>
        </div>
        <div className="module" id="recentDetections">
          <h2>Most Recent Detections</h2>
          <ul>
            {recentDetections.map((detection, index) => (
              <li key={index}>{detection}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
