import React, { useEffect } from 'react';
import { Line, Bar } from 'react-chartjs-2';

const Dashboard = () => {
  const [scoreData, setScoreData] = React.useState([]);
  const [severityData, setSeverityData] = React.useState([0, 0, 0, 0]);
  const [entropyData, setEntropyData] = React.useState([]);
  const [entropyLabels, setEntropyLabels] = React.useState([]);
  const [scoreLabels, setScoreLabels] = React.useState([]);
  const [newDetections, setNewDetections] = React.useState([]);
  const [topNotableSources, setTopNotableSources] = React.useState([]);
  const [recentDetections, setRecentDetections] = React.useState([]);
  const [alertColor, setAlertColor] = React.useState('#606060'); // Removed networkStatus

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:5001/ws');

    ws.onopen = () => {
      console.log('WebSocket connection opened');
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log('Received data:', data);

      if (data.entropy != undefined) {
	console.log('Received entropy:', data.entropy);
      }

      setAlertColor(getAlertColor(data.network_alert));
      updateRealtimeChart(data.entropy);
      setNewDetections(data.new_detections);
      setTopNotableSources(data.top_notable_sources);
      setSeverityData(data.detection_severity);
      updateScoreChart(data.current_score);
      setRecentDetections(data.most_recent_detections);
    };

    ws.onclose = () => {
      console.log('WebSocket connection closed');
    };

    ws.onerror = (error) => {
      console.log('WebSocket error:', error);
    };

    return () => {
      ws.close(); // Clean up WebSocket connection on unmount
    };
  }, []);

  const getAlertColor = (level) => {
    switch (level) {
      case 'Fetching Data': return '#606060';
      case 'Low': return '#008000';
      case 'Medium': return '#FFA500';
      case 'High': return '#FF4500';
      case 'HIGH ALERT!': return '#B22222';
      default: return '#606060';
    }
  };

  const updateRealtimeChart = (value) => {
    setEntropyData(prevData => {
      const newData = [...prevData, value];
      return newData.length > 8 ? newData.slice(1) : newData; // Keep last 8 data points (40 seconds of data at 5-second intervals)
    });
    setEntropyLabels(prevLabels => {
      const newLabels = [...prevLabels, new Date().toLocaleTimeString()];
      return newLabels.length > 8 ? newLabels.slice(1) : newLabels;
    });
  };

  const updateScoreChart = (score) => {
    setScoreData(prevData => {
      const newData = [...prevData, score];
      return newData.length > 8 ? newData.slice(1) : newData;
    });
    setScoreLabels((prevLabels) => {
      const newLabels = [...prevLabels, new Date().toLocaleTimeString()];
      return newLabels.length > 8 ? newLabels.slice(1) : newLabels;
    });
  };

  const scoreChartData = {
    labels: scoreLabels,
    datasets: [{
      label: 'Current Score',
      data: scoreData,
      backgroundColor: 'rgba(255, 99, 132, 0.2)',
      borderColor: 'rgba(255, 99, 132, 1)',
      borderWidth: 2,
      pointRadius: 0, // Remove dots
      fill: false
    }]
  };

  const severityChartData = {
    labels: ['Low', 'Medium', 'High', 'Critical'],
    datasets: [{
      label: 'Detections by Severity',
      data: severityData,
      backgroundColor: [
        'rgba(0, 0, 255, 0.75)', // Bolder blue
        'rgba(255, 165, 0, 0.75)', // Bolder orange
        'rgba(255, 69, 0, 0.75)', // Bolder orange red
        'rgba(255, 0, 0, 0.75)' // Bolder red
      ],
      borderColor: [
        'rgba(0, 0, 255, 0.5)', // Bolder blue
        'rgba(255, 165, 0, 0.5)', // Bolder orange
        'rgba(255, 69, 0, 0.5)', // Bolder orange red
        'rgba(255, 0, 0, 0.5)' // Bolder red
      ],
      borderWidth: 1
    }]
  };

  const entropyChartData = {
    labels: entropyLabels,
    datasets: [{
      label: 'Entropy',
      data: entropyData,
      backgroundColor: 'rgba(54, 162, 235, 0.5)',
      borderColor: 'rgba(0, 0, 139, 1)', // Darker blue
      borderWidth: 2,
      pointRadius: 0, // Remove dots
      fill: false
    }]
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false // Hide legend
      }
    },
    scales: {
      x: {
        grid: {
          display: false // Hide X grid lines
        }
      },
      y: {
        grid: {
          display: false // Hide Y grid lines
        }
      }
    }
  };

  const entropyChartOptions = {
    ...chartOptions,
    scales: {
      ...chartOptions.scales,
      y: {
        ...chartOptions.scales.y,
        min: -1,
        max: 1
      }
    },
    layout: {
      padding: {
        top: 20,
        bottom: 35 // Adjust padding to reduce the height of the chart
      }
    }
  };

  return (
    <div>
      <header>
        <div className="logo">
          <a href="#">Omen & Weave</a>
        </div>
        <nav>
          <ul>
            <li><a href="#">Platform</a></li>
            <li><a href="#">Services</a></li>
            <li><a href="#">Why Omen & Weave</a></li>
            <li><a href="#">Learn</a></li>
            <li><a href="#">Company</a></li>
          </ul>
        </nav>
        <div className="header-buttons">
          <button>Contact Us</button>
          <button>View Bundles & Pricing</button>
        </div>
      </header>
      <div className="dashboard-header">
        <h1>Detections Dashboard</h1>
        <div id="networkStatus" className="circle" style={{ backgroundColor: alertColor }}></div>
      </div>
      <div className="dashboard">
        <div className="module" id="currentScore">
          <h2>Current OWL Score</h2>
          <div className="chart-container">
            <Line data={scoreChartData} options={chartOptions} />
          </div>
        </div>
        <div className="module" id="newDetections">
          <h2>New Detections</h2>
          <ul>
            {newDetections.map((detection, index) => (
              <li key={index}>{detection}</li>
            ))}
          </ul>
        </div>
        <div className="module" id="mostRecentDetections">
          <h2>Most Recent Detections</h2>
          <ul>
            {recentDetections.map((detection, index) => (
              <li key={index}>{detection}</li>
            ))}
          </ul>
        </div>
        <div className="module chart-container">
          <h2>Entropy Over Time</h2>
          <Line data={entropyChartData} options={entropyChartOptions} />
        </div>
        <div className="module" id="topNotableSources">
          <h2>Top Notable Sources</h2>
          <ul>
            {topNotableSources.map((source, index) => (
              <li key={index}>{source}</li>
            ))}
          </ul>
        </div>
        <div className="module" id="detectionSeverity">
          <h2>Detections by Severity</h2>
          <div className="chart-container">
            <Bar data={severityChartData} options={chartOptions} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
